import React, { Component } from "react";

class BlogForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: props.editblog ? props.editblog.title : "",
      desc: props.editblog ? props.editblog.desc : "",
      err: "",
    };
  }
  onTitleChange = (e) => {
    const title = e.target.value;
    this.setState(() => ({ title }));
  };

  onDescChange = (e) => {
    const desc = e.target.value;
    this.setState(() => ({ desc }));
  };
  onBlogSubmit = (e) => {
    e.preventDefault();
    if (!this.state.title || !this.state.desc) {
      this.setState({ err: "Please, fill input." });
    } else {
      this.setState({ err: "" });
      this.props.onFormSubmit({
        title: this.state.title,
        desc: this.state.desc,
      });
    }
  };
  render() {
    return (
      <div>
        {this.state.err && (
          <p className="text-center alert-danger">{this.state.err}</p>
        )}
        <div className="container mt-5 d-flex align-items-center justify-content-center ">
          <form onSubmit={this.onBlogSubmit} className="col-6">
            <div class="mb-3">
              <label class="form-label">Add Photo</label>
              <input
                value={this.state.title}
                onChange={this.onTitleChange}
                type="text"
                class="form-control"
              />
            </div>
            <div class="mb-3">
              <label class="form-label">Add Title</label>
              <input
                value={this.state.title}
                onChange={this.onTitleChange}
                type="text"
                class="form-control"
              />
            </div>
            {/* {this.state.myTitle} -
        {this.state.desc} */}
            <div class="mb-3">
              <label class="form-label">Add Description</label>
              <textarea
                onChange={this.onDescChange}
                type="text"
                class="form-control"
              >
                {this.state.desc}
              </textarea>
            </div>

            <button type="submit" class="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default BlogForm;
